import { Component } from '@angular/core';
import { ReactiveFormsModule , FormBuilder, Validators } from '@angular/forms';

@Component({
  selector: 'app-contato',
  templateUrl: './contato.component.html',
  styleUrls: ['./contato.component.css']
})
export class ContatoComponent {
  formContato = this.fb.group({
    nome: ["", [
      Validators.minLength(4),
      Validators.required
    ]],
    assunto: ["", [
      Validators.minLength(10),
      Validators.required
    ]],
    telefone: ["", [
      Validators.minLength(11),
      Validators.required
    ]],
    email: ["", [
      Validators.email,
      Validators.required
    ]],
    mensagem: ["", [
      Validators.minLength(20),
      Validators.required
    ]]
  });

  constructor(
    private fb: FormBuilder
  ) { }

}
<h2 class="contact__title">Entre em contato</h2>

<section class="contact__container">
  <form class="contact-form" [formGroup]="formContato">
    <input type="text" placeholder="Digite o seu nome">
    <input type="assunto" placeholder="Digite o assunto">
    <input type="tel" placeholder="(00) 0 0000-0000">
    <input type="email" placeholder="Digite o seu e-mail">
    <textarea maxlength="300" placeholder="Digite sua mensagem..."></textarea>
    <button>Enviar</button>
  </form>

  <address>
    <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d234223.4252783426!2d-47.57687641505253!3d-23.46980443851741!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x94c58abceb48f503%3A0x947f99ac59d0781b!2sSorocaba%20-%20SP!5e0!3m2!1spt-PT!2sbr!4v1680619652756!5m2!1spt-PT!2sbr" width="600" height="450" style="border:0;" allowfullscreen="" loading="lazy" referrerpolicy="no-referrer-when-downgrade"></iframe>
    <p>R. 123, 10 - 21° andar - Centro, Sorocaba - SP, 00000-000</p>
  </address>
</section>
